import React, { useEffect, useState } from "react";
import axios from "axios";
import withMainLayout from "../layout/withMainLayout";
import { Link} from "react-router-dom";


function Home(){
    const URL = "http://localhost:3001";
    const [data, setData] = useState([]);
    const DEFAULT_EVENT = {id: "", date: "", time: "", place: "", description: ""}
    const[newEvent,setNewEvent] = useState(DEFAULT_EVENT);


    useEffect(()=>{
        loadData();
    }, []);

    function loadData()
    {
        axios.get("http://localhost:3000/events")
        .then(response => {
            response.data.sort((a,b) => a.id - b.id);
            setData(response.data);
        })
    }
    function handleDelete(id)
    {
        axios.delete(`http://localhost:3000/events/${id}`)
        .then(response =>{
            loadData();
        })
    }
    function EditForm(){
        
        function handleInput(event)
        {
            const {name,value} = event.target;
            setNewEvent({...newEvent, [name]: value})
        }

        function submitForm(){
            
            const url = `http://localhost:3000/events/${id}`;
            if(newEvent !== DEFAULT_EVENT)
            {
                axios.put(url,newEvent);
            }
            
        }
        return(
            <div className="container">
                <h1 className="text-center mt-3">Edit current event</h1>
                <form>
                    <div className="mt-3">
                        <label class="form-label">Date:</label>
                        <input onInput={e=>handleInput(e)} value={newEvent.date} placeholder="date" className="form-control" type="date" name="date"/>
                    </div>
                    <div className="mt-3">
                        <label class="form-label">Time:</label>
                        <input onInput={e=>handleInput(e)} value={newEvent.time} placeholder="time" className="form-control" type="time" name="time"/>
                    </div>
                    <div className="mt-3">
                        <label class="form-label">Place:</label>
                        <input onInput={e=>handleInput(e)} value={newEvent.place} placeholder="place" className="form-control" type="text" name="place"/>
                    </div>
                    <div className="mt-3">
                        <label class="form-label">Description:</label>
                        <input onInput={e=>handleInput(e)} value={newEvent.description} placeholder="description" className="form-control" type="text" name="description"/>
                    </div>
                    <div className="mt-3">
                        <Link to={"/"}><button onClick={()=>submitForm()} type="button" className="btn btn-primary">Edit</button></Link>
                    </div>
                </form>
            </div>
        )
    }
    
    return(
        <div>
            <table className="table table-bordered text-center">
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Date</th>
                        <th>TIme</th>
                        <th>Place</th>
                        <th>Description</th>
                        <th>Events</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((x, index) => (
                    <tr key={index}>
                        <td>{x.id}</td>
                        <td>{x.date}</td>
                        <td>{x.time}</td>
                        <td>{x.place}</td>
                        <td>{x.description}</td>
                        <td>
                        <button onClick={()=>EditForm(x.id)} className="btn btn-warning"><i className="fa fa-pencil"></i></button>
                        <button onClick={()=>handleDelete(x.id)} className="btn btn-danger ms-3"><i className="fa fa-x"></i></button>
                        </td>
                    </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}
export default withMainLayout(Home);
