import React from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
export default function withMainLayout(Component){
    
    function layout(props){
        return(
            <>
            <div className="mb-3">
                <Header />
            </div>
                <Component {...props}/>
            <div>
                <Footer />
            </div>
            </>
        )
    }
    return layout;
}
