    }
    return(
        <div>
            <table className="table table-bordered text-center">
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Date</th>
                        <th>TIme</th>
                        <th>Place</th>
                        <th>Description</th>
                        <th>Events</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((x, index) => (
                    <tr key={index}>
                        <td>{x.id}</td>
                        <td>{x.date}</td>
                        <td>{x.time}</td>
                        <td>{x.place}</td>
                        <td>{x.description}</td>
                        <td>
                        <button onClick={()=>handleEdit(x.id)} className="btn btn-warning"><i className="fa fa-pencil"></i></button>
                        <button onClick={()=>handleDelete(x.id)} className="btn btn-danger ms-3"><i className="fa fa-x"></i></button>
                        </td>
                    </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}
export default withMainLayout(Home);
