import React, { useEffect, useState } from "react";
import axios from "axios";
import withMainLayout from "../layout/withMainLayout";
import { Link } from "react-router-dom";
import Form from "../components/Form";

function Home(){
    const [data, setData] = useState([]);
    const [isEdit,setIsEdit] = useState(false);
    useEffect(()=>{
        loadData();
    }, []);

    function loadData()
    {
        axios.get("http://localhost:3000/events")
        .then(response => {
            response.data.sort((a,b) => a.id - b.id);
            setData(response.data);
        })
    }
    function handleDelete(id)
    {
        axios.delete(`http://localhost:3000/events/${id}`)
        .then(response =>{
            loadData();
        })
    }

    function handleEdit(idToSet)
    {
      
        setIsEdit(true)
      console.log(5);

    }
    return(
        <div>
            <table className="table table-bordered text-center">
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Date</th>
                        <th>TIme</th>
                        <th>Place</th>
                        <th>Description</th>
                        <th>Events</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((x, index) => (
                    <tr key={index}>
                        <td>{x.id}</td>
                        <td>{x.date}</td>
                        <td>{x.time}</td>
                        <td>{x.place}</td>
                        <td>{x.description}</td>
                        <td>
                        {<Link to={"/edit"}><button onClick={()=>handleEdit(x.id)} className="btn btn-warning"><i className="fa fa-pencil"></i></button></Link>}
                        
                        <button onClick={()=>handleDelete(x.id)} className="btn btn-danger ms-3"><i className="fa fa-x"></i></button>
                        </td>
                    </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}
export default withMainLayout(Home);
